select crew.ssn, crew.name from crew where crew.ssn not in (select ssn from reservation where code in(select tripinfo.code from tripinfo where tripinfo.origin = 'losangeles' or tripinfo.destination = 'losangeles')) and crew.ssn not in (select ssn from schedule where code in(select tripinfo.code from tripinfo where tripinfo.origin = 'losangeles' or tripinfo.destination = 'losangeles'));