select crew.ssn, crew.name from crew where crew.ssn not in (select distinct reservation.ssn from reservation inner join (select departure.code, departure.date from departure where departure.busnbr = 'ABC123') k on reservation.code = k.code and reservation.date = k.date) and crew.ssn not in (select distinct schedule.ssn from schedule inner join (select departure.code, departure.date from departure where departure.busnbr = 'ABC123') k on schedule.code = k.code and schedule.date = k.date);